# ====================
# AIへの指示: Usage, Options, 説明を記述
show_help() {
  cat << EOF
{{scriptDescription}}

Usage:
  $(basename "$0") [OPTIONS]

Options:
  --input <path>   入力パス（必須）
  --output <path>  出力パス
  -h, --help       ヘルプ表示

EOF
}

# ====================
# 引数パース
# ====================
# AIへの指示: 必要な引数を追加・修正
parse_args() {
  while [[ $# -gt 0 ]]; do
    case "$1" in
      --input)
        INPUT="$2"
        shift 2
        ;;
      --output)
        OUTPUT="$2"
        shift 2
        ;;
      -h|--help)
        show_help
        exit $EXIT_SUCCESS
        ;;
      *)
        log_error "不明なオプション: $1"
        exit $EXIT_ARGS_ERROR
        ;;
    esac
  done
}

